// Octicon Enhancement Script for NeMo RL Documentation

document.addEventListener('DOMContentLoaded', function() {
    // Enhance octicon rendering
    enhanceOcticons();
    
    // Re-run enhancement after dynamic content loads
    const observer = new MutationObserver(function(mutations) {
        mutations.forEach(function(mutation) {
            if (mutation.addedNodes.length) {
                enhanceOcticons();
            }
        });
    });
    
    observer.observe(document.body, {
        childList: true,
        subtree: true
    });
});

function enhanceOcticons() {
    // Find all octicon elements
    const octicons = document.querySelectorAll('.octicon[data-icon]');
    
    octicons.forEach(function(octicon) {
        const iconName = octicon.getAttribute('data-icon');
        const size = octicon.getAttribute('data-size') || '1em';
        
        // Create SVG icon based on the icon name
        const svgIcon = createOcticonSVG(iconName, size);
        
        // Replace the octicon content with SVG
        if (svgIcon && !octicon.querySelector('svg')) {
            octicon.innerHTML = svgIcon;
        }
    });
}

